def solution(stdin: str) -> str:
    def rotate(text, amount):
        return "".join(chr((ord(char) - 65 + amount) % 26 + 65) for char in text)

    def split_and_rotate(text):
        mid = len(text) // 2
        left, right = text[:mid], text[mid:]
        rotation_value = sum(ord(char) - 64 for char in left)
        return rotate(left, rotation_value), rotate(right, rotation_value)

    def merge(left, right):
        return "".join(rotate(char, ord(right[i]) - 64) for i, char in enumerate(left))

    left, right = split_and_rotate(stdin)
    return merge(left, right)

def solution(stdin: str) -> str:
    word = stdin
