    anagrams = set()
    for i in range(n):
        for j in range(i+1, n+1):
            anagrams.add(''.join(sorted(word[i:j])))
    palindromes = set()
    for anagram in anagrams:
        if anagram == anagram[::-1]:
            palindromes.add(anagram)
    min_removals = n
    for i in range(n):
        for j in range(i+1, n+1):
            if ''.join(sorted(word[i:j])) in palindromes:
                min_removals = min(min_removals, n - j + i)
    return min_removals

