def solution(stdin: str) -> str:
    # Parse input
    N, M, A, B, K, G = map(int, stdin.split('\n')[0].split())
    streets = []
    for i in range(M):
        street = list(map(int, stdin.split('\n')[i+2].split()))
        streets.append(street)
    intersections = [[] for _ in range(N+1)]
    for street in streets:
        intersections[street[0]].append((street[1], street[2]))

    visited = [False] * (N+1)
    arrival_time = [float('inf')] * (N+1)
    arrival_time[A] = K

    queue = [(A, 0)]
    while queue:
        intersection, time = queue.pop(0)
        if visited[intersection]:
            continue
        visited[intersection] = True
        for next_intersection, street_time in intersections[intersection]:
            if not visited[next_intersection]:
                arrival_time[next_intersection] = min(arrival_time[next_intersection], time + street_time)
                queue.append((next_intersection, time + street_time))

    return str(arrival_time[B] - K)

def solution(stdin: str) -> str:
    action, message = stdin.split(" ", 1)

    if action == "E":
        encoded_message = ""
        count = 1
        for i in range(1, len(message)):
            if message[i] == message[i - 1]:
                count += 1
            else:
                encoded_message += message[i - 1] + str(count)
                count = 1
        encoded_message += message[-1] + str(count)
        return encoded_message

    elif action == "D":
        decoded_message = ""
        i = 0
        while i < len(message):
            char = message[i]
            count = int(message[i + 1])
            decoded_message += char * count
            i += 2
        return decoded_message

    else:
        return "Invalid input"

