            stored = True

        if not stored and B[i] not in used_drawers:
            used_drawers.add(B[i])
            res.append("LADICA")
            stored = True

        if not stored:
            start_A, start_B = A[i], B[i]
            while not stored:
                if start_A == A[i] and start_B == B[i]:
                    break
                if start_A not in used_drawers:
                    used_drawers.add(start_A)
                    res.append("LADICA")
                    stored = True
                if not stored:
                    start_A = A[A.index(start_A)]

            if not stored:
                start_B = B[B.index(start_B)]
                while not stored:
                    if start_A == A[i] and start_B == B[i]:
                        break
                    if start_B not in used_drawers:
