    def bfs(start):
        queue = [start]
        visited = set()
        depth = 0
        while queue:
            depth += 1
            for _ in range(len(queue)):
                r, c = queue.pop(0)
                if (r, c) == (1, 1):
                    return depth
                if (r, c) not in visited and board[r-1][c-1] != '#':
                    visited.add((r, c))
