def solution(stdin: str) -> str:
    def generate_table(key_phrase: str) -> List[str]:
        key = ""
        for char in key_phrase:
            if char not in key and char.isalpha() and char.lower() != 'q':
                key += char.upper()
        for char in 'ABCDEFGHIJKLMNOPQRSTUVWXYZ':
            if char not in key:
                key += char
        table = [key[i:i+5] for i in range(0, 25, 5)]
        return table

    def encrypt(table: List[str], plaintext: str) -> str:
        ciphertext = ""
        plaintext = plaintext.upper().replace("J", "I")
        plaintext = ''.join(char for char in plaintext if char.isalpha())
        plaintext = 'X' * (len(plaintext) % 2 == 1) + plaintext

        for i in range(0, len(plaintext), 2):
            pair = plaintext[i:i+2]
            if pair[0] == pair[1]:
                pair = pair[0] + 'X' + pair[1]
            row1, col1 = divmod(table[0].index(pair[0]), 5)
            row2, col2 = divmod(table[0].index(pair[1]), 5)

            if row1 == row2:
                ciphertext += table[row1][(col1 + 1) % 5] + table[row2][(col2 + 1) % 5]
            elif col1 == col2:
                ciphertext += table[row1][col1] + table[(row1 + 1) % 5][col1]
            else:
                ciphertext += table[row1][col2] + table[row2][col1]

        return ciphertext

    key_phrase, plaintext = stdin.strip().split('\n')
    table = generate_table(key_phrase)
    ciphertext = encrypt(table, plaintext)

    return ciphertext

