    def safe_order(n: int, s: List[int]) -> List[int]:
        pile = sum(s)
        visited = [False] * n
        safe_order = []

        for i in range(n):
            if pile < s[i]:
                return "impossible"

            if i > 0:
                pile -= s[i - 1]

            safe_order.append(i + 1)
            visited[i] = True
            pile += s[i]

            for j in range(i + 1, n):
                if not visited[j] and pile < s[j]:
                    return "impossible"

            if i == n - 2:
                if pile < s[0]:
                    return "impossible"

                pile -= s[0]
                safe_order.append(1)
                visited[0] = True

        return safe_order

