def solution(stdin: str) -> str:
    def find(parent, i):
        if parent[i] == i:
            return i
        parent[i] = find(parent, parent[i])
        return parent[i]

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    n = int(stdin.readline())
    parent = list(range(n + 1))
    rank = [0] * (n + 1)
    edges = []

    for i in range(n - 1):
        a, b = map(int, stdin.readline().split())
        edges.append((a, b))

    edges.sort(key=lambda x: -max(rank[find(parent, x[0])], rank[find(parent, x[1])]))

    max_edges = 0
    for a, b in edges:
        if union(parent, rank, a, b):
            max_edges += 1

    for i in range(1, n + 1):
        find(parent, i)

    components = {}
    for i in range(1, n + 1):
        if parent[i] not in components:
            components[parent[i]] = set()
        components[parent[i]].add(i)

    for a, b in edges:
        if parent[a] != parent[b]:
            components[parent[a]].update(components[parent[b]])
            del components[parent[b]]

    max_vertices = 0
    max_a, max_b, max_c = 0, 0, 0
    for a, b in edges:
        if parent[a] == parent[b]:
            if len(components[parent[a]]) > max_vertices:
                max_vertices = len(components[parent[a]])
                max_a, max_b, max_c = a, b, list(components[parent[a]])[0]
            elif len(components[parent[a]]) == max_vertices:
                for c in components[parent[a]]:
                    if c not in (a, b):
                        if a < c < b or a > c > b:
                            max_a, max_b, max_c = a, c, b
                            break

    return f"{max_edges}\n{max_a} {max_c} {max_b}\n"

