    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    def is_divisible(subset_sum):
        return subset_sum % k == 0

    def helper(a, row, col, k, current_sum, used):
        if used > m // 2 or row == n:
            return current_sum if is_divisible(current_sum) else None

        if is_divisible(current_sum):
            return current_sum

        exclude = helper(a, row + 1, col + 1, k, current_sum, used)
        include = helper(a, row + 1, col + 1, k, current_sum + a[row][col], used + 1)

        if exclude is None and include is None:
            return None

        if include is not None and (exclude is None or include > exclude):
            return include
        else:
            return exclude

    return helper(a, 0, 0, k, 0, 0) or 0

