def solution(stdin: str) -> str:
    def find_result(a, b, c):
        res = 0
        A, B, C = a, b, c
        while B % A != 0:
            B += 1
            res += 1
        while C % B != 0:
            C += 1
            res += 1
        return res, A, B, C

    def find_result_reverse(a, b, c):
        res = 0
        A, B, C = a, b, c
        while C % B != 0:
            A -= 1
            res += 1
        while B % A != 0:
            B -= 1
            res += 1
        return res, A, B, C

    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    t = int(stdin.readline())
    output = []
    for i in range(t):
        a, b, c = map(int, stdin.readline().split())
        res, A, B, C = find_result(a, b, c)
        if res == 0:
            output.append(f"{res}\n{A} {B} {C}")
        else:
            res_reverse, A_reverse, B_reverse, C_reverse = find_result_reverse(a, b, c)
            if res_reverse == 0:
                output.append(f"{res_reverse}\n{A_reverse} {B_reverse} {C_reverse}")
            else:
                g = gcd(a, gcd(b, c))
                res_gcd = res + res_reverse - 2 * (b // g - a)
                A_gcd = a
                B_gcd = b // g
                C_gcd = c // g
                output.append(f"{res_gcd}\n{A_gcd} {B_gcd} {C_gcd}")
    return "\n".join(output)

