def solution(stdin: str) -> str:
    def can_prepare(exam, day):
        return exam[0] <= day < exam[1]

    def can_pass(exam, day):
        return exam[1] == day

    def is_rest_day(day):
        return not any(can_prepare(exam, day) or can_pass(exam, day) for exam in exams)

    def prepare_for_exam(exam, day):
        for i, e in enumerate(exams):
            if e == exam:
                return i + 1
        return -1

    def can_be_done(plan):
        days = [False] * (n + 1)
        for day, activity in enumerate(plan):
            if activity == 0:
                days[day] = True
            elif activity > 0:
                exam = exams[activity - 1]
                if not can_pass(exam, day):
                    return False
                if any(can_prepare(exam, d) for d in range(exam[0], exam[1])):
                    return False
                if not all(days[d] for d in range(exam[0], exam[1])):
                    return False
                days[exam[0]:exam[1]] = [True] * (exam[1] - exam[0])
            else:
                return False
        return all(days)

    n, m = map(int, stdin.readline().split())
    exams = [tuple(map(int, stdin.readline().split())) for _ in range(m)]
    plan = [0] * (n + 1)

    for day in range(1, n + 1):
        if is_rest_day(day):
            plan[day] = 0
        else:
            for exam in exams:
                if can_pass(exam, day):
                    plan[day] = prepare_for_exam(exam, day)
                    break
            else:
                plan[day] = -1

    if can_be_done(plan):
        return ' '.join(map(str, plan))
    else:
        return '-1'

