    def build_graph(n, m, edges):
        graph = [[] for _ in range(n + 1)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)
        return graph

    def find_spanning_tree(n, m, graph, target_degree):
        visited = [False] * (n + 1)
        stack = [(1, 0)]
        spanning_tree = []

        while stack:
            v, parent = stack.pop()
            if not visited[v]:
                visited[v] = True
                if parent:
                    spanning_tree.append((parent, v))

                for u in graph[v]:
                    if not visited[u]:
                        stack.append((u, v))

        degree = [0] * (n + 1)
        for u, v in spanning_tree:
            degree[u] += 1
            degree[v] += 1

        if degree[1] == target_degree:
            return spanning_tree
        else:
            return None

    input_data = list(map(int, stdin.split()))
    n, m, target_degree = input_data[0], input_data[1], input_data[2]
    edges = [tuple(map(int, stdin.split())) for i in range(2, m + 2)]

    graph = build_graph(n, m, edges)
    spanning_tree = find_spanning_tree(n, m, graph, target_degree)

    if spanning_tree:
        return "YES\n" + "\n".join(" ".join(map(str, edge)) for edge in spanning_tree)
    else:
        return "NO"

