    def segment_intersection(a, b, c, d):
        if a > c or b < d:
            return 0, None, None
        if a <= c and d <= b:
            return d - c, c, d
        if c <= a and b <= d:
            return b - a, a, b
        if c < a:
            return b - c, c, b
        return d - a, a, d

    n = int(stdin.readline())
