    for _ in range(n):
        l, r = map(int, stdin.readline().split())
        segments.append((l, r))

    max_length = 0
    for i in range(n):
        current_intersection = [segments[i]]
        for j in range(n):
            if i != j:
                length, start, end = segment_intersection(current_intersection[-1][0], current_intersection[-1][1], segments[j][0], segments[j][1])
                if length > 0:
                    current_intersection.append((start, end))
        max_length = max(max_length, current_intersection[-1][1] - current_intersection[-1][0])

