    s = stdin.readline().strip()

    if k > n:
        return -1

    if k == 1:
        return 0

    def is_subsequence(t, s):
        t = iter(t)
        return all(c in t for c in s)

    def subsequences(s):
        return set(s[i:j] for i in range(len(s)) for j in range(i + 1, len(s) + 1))

    def min_cost(k, s):
        if k == 0:
            return 0

        min_cost = float('inf')
        for t in subsequences(s):
            if is_subsequence(t, s) and len(t) == len(s) - k + 1:
                min_cost = min(min_cost, len(s) - len(t))

        return min_cost

    return min_cost(k, s)

