def solution(stdin: str) -> str:
    n, s = stdin.split("\n")
    n = int(n)

    MOD = 1000000007
    dp = [[0] * (2 * n + 1) for _ in range(2 * n + 1)]

    for i in range(2 * n + 1):
        for j in range(2 * n + 1):
            if i == j:
                dp[i][j] = 1
            elif j == i + 1:
                dp[i][j] = 1
            else:
                for k in range(i + 1, j, 2):
                    dp[i][j] += dp[i][k] * dp[k][j]
                    dp[i][j] %= MOD

    def is_valid(s: str) -> bool:
        stack = []
        for c in s:
            if c == "(":
                stack.append(c)
            else:
                if not stack:
                    return False
                stack.pop()
        return not stack

    valid_brackets = [s[i:j] for i in range(len(s)) for j in range(i + 1, len(s) + 1, 2) if is_valid(s[i:j])]

    def count_sequences(length: int, brackets: list) -> int:
        if length == len("".join(brackets)):
            return 1
        if length < len("".join(brackets)):
            return 0
        result = 0
        for i in range(len(brackets)):
            if brackets[i] == "(":
                result += dp[length - len(brackets) + i][length - i] * count_sequences(length - i, brackets[:i] + brackets[i + 1:])
                result %= MOD
            else:
                break
        return result

    result = 0
    for bracket in valid_brackets:
        result += count_sequences(2 * n, list(bracket))
        result %= MOD

    return str(result)

