    def swap_adjacent_digits(num: int) -> int:
        num_str = str(num)
        for i in range(len(num_str) - 1):
            if num_str[i] > num_str[i + 1]:
                num_str = num_str[:i] + num_str[i + 1] + num_str[i] + num_str[i + 2:]
                return int(num_str)
        return num

    while n % 25 != 0:
        n = swap_adjacent_digits(n)
        if n == original_n:
            return -1
        moves += 1

    return moves

def solution(stdin: str) -> str:
    n = int(stdin.readline().strip())
    s = stdin.readline().strip()

    def can_be_sorted(s: str, c: List[int]) -> bool:
        for i in range(n - 1):
            if c[i] != c[i + 1] and s[i] > s[i + 1]:
                return False
        return True

    def dfs(s: str, c: List[int], idx: int) -> int:
        if idx == n:
            return 0 if can_be_sorted(s, c) else float('inf')
        min_cost = float('inf')
        for color in range(1, len(c) + 1):
            c[idx] = color
            cost = dfs(s, c, idx + 1)
            if cost != float('inf'):
                min_cost = min(min_cost, cost + 1)
        return min_cost

    c = [0] * n
    min_colors = dfs(s, c, 0) + 1

    return f"{min_colors}\n{' '.join(map(str, c))}"

