            layer_size = k
            for i in range(1, d):
                layers.append([j + n for j in range(layer_size)])
                layer_size += k
            return layers

        layers = generate_layers(n, d, k)
        edges = [(1, layers[0][1])]
        for i in range(d - 1):
            edges.extend(connect_layers(layers[:i + 1], k))

        return edges

