        max_diff = float('-inf')
        for subset in itertools.chain.from_iterable(itertools.combinations(segments, r) for r in range(len(segments) + 1)):
            if not subset:
                continue
            b = a.copy()
            for l, r in subset:
                b[l - 1:r] = [x - 1 for x in b[l - 1:r]]
            diff = max(b) - min(b)
            max_diff = max(max_diff, diff)
        return max_diff

