        max_diff = get_max_diff(n, m, a, segments)
        subsets = []
        for subset in itertools.chain.from_iterable(itertools.combinations(range(1, m + 1), r) for r in range(m + 1)):
            if not subset:
                continue
            b = a.copy()
            for i in subset:
                l, r = segments[i - 1]
                b[l - 1:r] = [x - 1 for x in b[l - 1:r]]
            diff = max(b) - min(b)
            if diff == max_diff:
                subsets.append(subset)
        subset = random.choice(subsets)
        return f"{max_diff}\n{len(subset)}\n{' '.join(map(str, subset))}\n"

