        for segment in segments:
            for i in range(segment[0] - 1, segment[1]):
                arr[i] -= 1
        return max(arr) - min(arr)

    def binary_search(arr, segments, target):
        left, right = 0, len(segments)
        while left < right:
            mid = (left + right) // 2
            if get_max_min_diff(arr[:], segments[:mid]) >= target:
                right = mid
            else:
                left = mid + 1
        return left

