                                matrix[i][j], matrix[k][j] = matrix[k][j], matrix[i][j]
                                moves += 1
                                break
            for j in range(m):
                while not matrix_is_good(matrix, n, m):
                    shift_column(matrix, j, n)
                    moves += 1
            for j in range(m):
