    def find_root(parent, city):
        if parent[city] == city:
            return city
        return find_root(parent, parent[city])

    def union(parent, rank, city1, city2):
        root1 = find_root(parent, city1)
        root2 = find_root(parent, city2)
        if root1 != root2:
            if rank[root1] < rank[root2]:
                parent[root1] = root2
            elif rank[root1] > rank[root2]:
                parent[root2] = root1
            else:
                parent[root2] = root1
                rank[root1] += 1

    def get_roads(n, k):
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)
        edges = []
        for _ in range(n - 1):
            x, y = map(int, stdin.readline().split())
            edges.append((x, y))
            union(parent, rank, x, y)

        bad_cities = []
        for i in range(1, n + 1):
            for j in range(i + 1, n + 1):
                if find_root(parent, i) == find_root(parent, j):
                    bad_cities.append(i)
                    bad_cities.append(j)
        bad_cities = list(set(bad_cities))
        if len(bad_cities) > k:
            return None

        road_to_company = {}
        r = 0
        for x, y in edges:
            if (x, y) not in road_to_company and (y, x) not in road_to_company:
                r += 1
                road_to_company[(x, y)] = r
                road_to_company[(y, x)] = r
            else:
                if (x, y) in road_to_company:
                    road_to_company[(y, x)] = road_to_company[(x, y)]
                else:
                    road_to_company[(x, y)] = road_to_company[(y, x)]

        return r, road_to_company

    n, k = map(int, stdin.readline().split())
    r = None
    road_to_company = None
    for i in range(1, k + 2):
        result = get_roads(n, i)
        if result is not None:
            r, road_to_company = result
            break

    if r is None:
        return "IMPOSSIBLE"

    output = [road_to_company[(x, y)] for x, y in sorted(road_to_company.keys())]
    return f"{r}\n{' '.join(map(str, output))}"

