def solution(stdin: str) -> str:
    H, W = map(int, stdin.readline().split())
    matrix = [list(stdin.readline().strip()) for _ in range(H)]

    def can_compress(direction: str) -> bool:
        if direction == "row":
            for row in matrix:
                if all(square == "." for square in row):
                    return True
        elif direction == "column":
            for j in range(W):
                if all(matrix[i][j] == "." for i in range(H)):
                    return True
        return False

    def compress(direction: str) -> None:
        if direction == "row":
            for i in range(H - 1, -1, -1):
                if all(matrix[i][j] == "." for j in range(W)):
                    del matrix[i]
        elif direction == "column":
            for j in range(W - 1, -1, -1):
                if all(matrix[i][j] == "." for i in range(H)):
                    for row in matrix:
                        del row[j]

    while can_compress("row") or can_compress("column"):
        if can_compress("row"):
            compress("row")
            H -= 1
        if can_compress("column"):
            compress("column")
            W -= 1

    return "\n".join(["".join(row) for row in matrix]) + "\n"

