def solution(stdin: str) -> str:
    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    def kruskal(n, m, edges):
        parent = [i for i in range(n)]
        rank = [0] * n
        mst_cost = 0
        mst_edges = []

        for edge in sorted(edges, key=lambda x: x[2]):
            u, v, w = edge
            if union(parent, rank, u - 1, v - 1):
                mst_cost += w
                mst_edges.append(edge)

        return mst_cost, mst_edges

    def increase_weight(n, m, edges):
        mst_cost, mst_edges = kruskal(n, m, edges)
        if len(mst_edges) == n - 1:
            return 0

        for i in range(m):
            u, v, w = edges[i]
            edges[i] = (u, v, w + 1)
            new_mst_cost, new_mst_edges = kruskal(n, m, edges)
            if len(new_mst_edges) == n - 1 and new_mst_cost == mst_cost:
                return i + 1
            edges[i] = (u, v, w)

        return -1

    lines = stdin.splitlines()
    n, m = map(int, lines[0].split())
    edges = [list(map(int, lines[i].split())) for i in range(1, m + 1)]

    result = increase_weight(n, m, edges)
    return str(result)

