        while len(equal_elements) > 1:
            min_value = min(equal_elements)
            for i in range(n):
                if a[i] == min_value:
                    if i > 0 and a[i - 1] != min_value:
                        operations.append((1, i - 1, i))
                    if i < n - 1 and a[i + 1] != min_value:
                        operations.append((1, i, i + 1))
            equal_elements.remove(min_value)
        return operations

    def apply_operations(a, operations):
        for operation in operations:
            if operation[0] == 1:
                a[operation[1]] += abs(a[operation[1]] - a[operation[2]])
            else:
                a[operation[1]] -= abs(a[operation[1]] - a[operation[2]])

    operations = get_operations(a)
    apply_operations(a, operations)

    if a.count(a[0]) == n:
        return f"{len(operations)}\n" + "\n".join(" ".join(map(str, operation)) for operation in operations)
    else:
