    def kruskal(edges, n, m):
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)

        def find(u):
            if u != parent[u]:
                parent[u] = find(parent[u])
            return parent[u]

        def union(u, v):
            pu, pv = find(u), find(v)
            if pu == pv:
                return False
            if rank[pu] > rank[pv]:
                parent[pv] = pu
            else:
                parent[pu] = pv
                if rank[pu] == rank[pv]:
                    rank[pv] += 1
            return True

        mst = []
        for e in edges:
            if union(e[0], e[1]):
                mst.append(e)
        return mst

    def parse_input(stdin: str) -> tuple:
        lines = stdin.split('\n')
        n, m = map(int, lines[0].split())
        edges = [tuple(map(int, line.split())) for line in lines[1:]]
        return n, m, edges

    n, m, edges = parse_input(stdin)
    mst = kruskal(edges, n, m)
    return '\n'.join(f'{u} {v}' for u, v in mst)

