    points = []
    for i in range(n):
        x, y = map(int, stdin.readline().split())
        points.append((x, y))

    points.sort(key=lambda p: max(p[0], p[1]), reverse=True)

    def distance(p1, p2):
        return abs(p1[0] - p2[0]) + abs(p1[1] - p2[1])

    def visit_points(points, start_level, visited=set(), current_point=(0, 0), current_distance=0):
        if not points:
            return current_distance

        max_level = max(max(p[0], p[1]) for p in points)
        if max_level > start_level:
            return float('inf')

        next_points = [p for p in points if p not in visited]
        if not next_points:
            return current_distance

        next_point = min(next_points, key=lambda p: distance(current_point, p))
        next_distance = distance(current_point, next_point)

        return min(visit_points(points, start_level, visited | {next_point}, next_point, current_distance + next_distance))

    return str(visit_points(points, max(max(p[0], p[1]) for p in points)))

