    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    N, M = map(int, stdin.readline().split())
    edges = [tuple(map(int, stdin.readline().split())) for _ in range(M)]

    parent = list(range(N + 1))
    rank = [0] * (N + 1)
    bridges = 0

    for u, v in edges:
        if union(parent, rank, u, v):
            bridges += 1

    return str(bridges)

