        while b:
            a, b = b, a % b
        return a

    def find_max_teams(n, weights):
        weights.sort()
        max_teams = 0
        s = 0

        for i in range(n - 1):
            if i > 0 and weights[i] == weights[i - 1]:
                continue
            j = i + 1
            total_weight = weights[i] + weights[j]
            gcd_value = gcd(weights[i], weights[j])

            while j < n and weights[j] == weights[j - 1]:
                j += 1
            if j == n:
