                if weights[k] >= total_weight:
                    break
                if weights[k] == weights[k - 1]:
                    k += 1
                    continue
                if total_weight % gcd(gcd_value, weights[k]) != 0:
                    k += 1
                    continue
                max_teams = max(max_teams, k - i + 1)
                k += 1

        return max_teams

