    roads = [list(map(int, stdin.readline().split())) for _ in range(m)]
    courier_routes = [list(map(int, stdin.readline().split())) for _ in range(k)]

    # Create adjacency matrix and cost matrix
    adj_matrix = [[0] * n for _ in range(n)]
    cost_matrix = [[0] * n for _ in range(n)]

    for x, y, w in roads:
        adj_matrix[x - 1][y - 1] = adj_matrix[y - 1][x - 1] = 1
        cost_matrix[x - 1][y - 1] = cost_matrix[y - 1][x - 1] = w

    # Floyd-Warshall algorithm for finding all-pairs shortest paths
    for i in range(n):
        for j in range(n):
            for l in range(n):
                if cost_matrix[j][l] > cost_matrix[j][i] + cost_matrix[i][l]:
                    cost_matrix[j][l] = cost_matrix[j][i] + cost_matrix[i][l]

    # Calculate total cost of each courier route
    total_cost = 0
    for a, b in courier_routes:
        total_cost += cost_matrix[a - 1][b - 1]

    # Find the road with the maximum cost
    max_cost_road = max(roads, key=lambda x: x[2])

    # Check if changing the cost of the road to zero reduces the total cost
    for i, j, w in roads:
        if (i, j) != (max_cost_road[0], max_cost_road[1]):
            cost_matrix[i - 1][j - 1] = cost_matrix[j - 1][i - 1] = 0
            new_total_cost = 0
            for a, b in courier_routes:
                new_total_cost += cost_matrix[a - 1][b - 1]
            if new_total_cost < total_cost:
                total_cost = new_total_cost

    return str(total_cost)

