        while queue:
            x, y = queue.pop(0)
            if (x, y) not in visited:
                visited.add((x, y))
                if x == target_x and y == target_y:
                    return True
                for i in range(2):
                    new_x, new_y = x + dx[i], y + dy[i]
                    if 0 <= new_x <= 1000 and 0 <= new_y <= 1000 and (new_x, new_y) not in visited:
                        queue.append((new_x, new_y))
        return False

    def backtrack(path, x, y, target_x, target_y, visited):
        if x == target_x and y == target_y:
            return path
        dx = [1, 0]
        dy = [0, 1]
        for i in range(2):
            new_x, new_y = x + dx[i], y + dy[i]
            if 0 <= new_x <= 1000 and 0 <= new_y <= 1000 and (new_x, new_y) not in visited:
                visited.add((new_x, new_y))
                new_path = backtrack(path + ['RU'[i]], new_x, new_y, target_x, target_y, visited)
                if new_path:
                    return new_path
        return None

