    def cross_product(a: tuple, b: tuple) -> tuple:
        x = a[1] * b[2] - a[2] * b[1]
        y = a[2] * b[0] - a[0] * b[2]
        z = a[0] * b[1] - a[1] * b[0]
        return (x, y, z)

    def dot_product(a: tuple, b: tuple) -> float:
        return sum(a[i] * b[i] for i in range(len(a)))

    def magnitude(a: tuple) -> float:
        return (sum(a[i] ** 2 for i in range(len(a))) ** 0.5)

    AB = tuple(B[i] - A[i] for i in range(len(A)))
    BC = tuple(C[i] - B[i] for i in range(len(B)))
    CD = tuple(D[i] - C[i] for i in range(len(C)))

    X = cross_product(AB, BC)
    Y = cross_product(BC, CD)

    cos_phi = dot_product(X, Y) / (magnitude(X) * magnitude(Y))
    phi = (180 / 3.14159) * (math.acos(cos_phi))

    return "{:.2f}".format(phi)

