        s = (a + b + c) / 2
        return round((s * (s - a) * (s - b) * (s - c)) ** 0.5, 5)

    def max_radius(A, B, C):
        r1, r2, r3 = A / 2, B / 2, C / 2
        return round(max(r1, r2, r3), 5)

    N = int(stdin.readline())
    triangles = []
    for i in range(N):
        A, B, C = map(int, stdin.readline().split())
        triangles.append((A, B, C))

    max_radii = [max_radius(*triangle) for triangle in triangles]
    max_radius_sum = sum(max_radii)

    min_rod_length = max_radius_sum * 2
    for i in range(N):
        for j in range(i + 1, N):
            distance = max_radii[i] + max_radii[j]
            if distance <= abs(triangles[i][0] - triangles[j][0]) or distance <= abs(triangles[i][1] - triangles[j][1]) or distance <= abs(triangles[i][2] - triangles[j][2]):
                min_rod_length = max(min_rod_length, distance * 2)

    return str(min_rod_length)

