    letters = "abcdefghijklmnopqrstuvwxyz"
    words = []

    def backtrack(remaining_weight, current_word):
        if remaining_weight == 0 and len(current_word) == l:
            words.append(current_word)
        elif remaining_weight < 0 or len(current_word) == l:
            return
        else:
            for letter in letters:
                backtrack(remaining_weight - (ord(letter) - ord('a') + 1), current_word + letter)

    backtrack(w, "")

    if words:
        return words[0]
    else:
        return "impossible"

