    def time_to_distribute_keys(c):
        return s * (1 + 1 / c) / v

    c = 1
    best_time = float('inf')
    best_c = 0

    while True:
        run_time = time_to_run_algorithm(c)
        distribute_time = time_to_distribute_keys(c)
        total_time = run_time + distribute_time

        if total_time < best_time:
            best_time = total_time
            best_c = c

        if distribute_time < run_time:
            c += 1
        else:
            break

    return f"{best_time:.6f} {best_c:.6f}"

