    def find_edge(start, visited):
        min_distance = float('inf')
        next_node = None
        for i in range(n):
            if i not in visited:
                distance = distance_matrix[start][i]
                if distance < min_distance:
                    min_distance = distance
                    next_node = i
        return next_node, min_distance

    edges = []
    visited = {0}
    current_node = 0
    while len(visited) < n:
        next_node, distance = find_edge(current_node, visited)
        edges.append((current_node + 1, next_node + 1, distance))
        visited.add(next_node)
        current_node = next_node

    output = ''
    for edge in edges:
        output += f'{edge[0]} {edge[1]}\n'
    return output.strip()

def solution(stdin: str) -> str:
    def rotate_string(s: str, rotation: int) -> str:
        return ''.join([chr(((ord(c) - ord('A') + rotation) % 26) + ord('A')) for c in s])

    def calculate_rotation(s: str) -> int:
        return sum([ord(c) - ord('A') for c in s])

    def merge_strings(s1: str, s2: str) -> str:
        return ''.join([rotate_string(s1[i], ord(s2[i]) - ord('A')) for i in range(len(s1))])

    def decrypt(s: str) -> str:
        mid = len(s) // 2
        s1, s2 = s[:mid], s[mid:]
        s1_rotated = rotate_string(s1, calculate_rotation(s2))
        s2_rotated = rotate_string(s2, calculate_rotation(s1))
        return merge_strings(s1_rotated, s2_rotated)

    return decrypt(stdin)

