def solution(stdin: str) -> str:
    # Function to check if the position is within the bounds of the chess grid
    def is_valid(x, y):
        return 1 <= x <= N and 1 <= y <= N

    def bfs(board, start):
        queue = [(start, 0)]
        visited = {start}

        while queue:
            (x, y), steps = queue.pop(0)

            for dx, dy in [(-2, 1), (-2, -1), (2, 1), (2, -1), (-1, 2), (-1, -2), (1, 2), (1, -2)]:
                new_x, new_y = x + dx, y + dy

                if is_valid(new_x, new_y) and board[new_x - 1][new_y - 1] != "#" and (new_x, new_y) not in visited:
                    if (new_x, new_y) == (1, 1):
                        return steps + 1

                    queue.append(((new_x, new_y), steps + 1))
                    visited.add((new_x, new_y))

        return -1

    # Read input
    N = int(stdin.readline().strip())
    board = [list(stdin.readline().strip()) for _ in range(N)]

    # Find the initial position of the knight
    start = None
    for i in range(N):
        for j in range(N):
            if board[i][j] == "K":
                start = (i + 1, j + 1)
                break

    # Run BFS from the initial position
    result = bfs(board, start)

    # Return the result
    return str(result)

