def solution(stdin: str) -> str:
    # Define the Playfair cipher matrix
    def create_matrix(key_phrase):
        matrix = []
        key = "".join(key_phrase.split())
        key = key.replace("q", "").replace("Q", "")
        key = key + "abcdefghijklmnoprstuvwxyz"
        for i in range(0, 25, 5):
            matrix.append(list(key[i:i+5]))
        return matrix

    # Define the mapping function
    def mapper(matrix, pair):
        if pair[0] == pair[1]:
            return "X" + pair[1]

        for i in range(5):
            if pair[0] in matrix[i]:
                x, y = matrix[i].index(pair[0]), matrix[i].index(pair[1])
                if x == y:
                    return matrix[i][(x+1) % 5] + matrix[(i+1) % 5][y]
                else:
                    return matrix[i][x] + matrix[i][y]

    # Read input
    key_phrase, plaintext = stdin.strip().split('\n')
    matrix = create_matrix(key_phrase)

    # Split plaintext into digraphs
    plaintext = plaintext.replace("j", "i").replace("J", "I")
    plaintext = plaintext.replace(" ", "")
    plaintext = [plaintext[i:i+2] for i in range(0, len(plaintext), 2)]

    # Encrypt digraphs
    encrypted_digraphs = []
    for digraph in plaintext:
        encrypted_digraph = mapper(matrix, digraph)
        encrypted_digraphs.append(encrypted_digraph)
    encrypted_text = "".join(encrypted_digraphs)

    # Return encrypted text in upper case
    return encrypted_text.upper()

