    def average_cost(average, total_shares, shares_sold, price_sold):
        if total_shares - shares_sold == 0:
            return 0
        return (average * total_shares - shares_sold * price_sold) / (total_shares - shares_sold)

    def process_split(average, shares, x):
        new_shares = shares * x
        return average / x, new_shares

    def process_merge(average, shares, x):
        new_shares = shares // x
        return average * x, new_shares

    lines = stdin.strip().split('\n')
    total_shares = 0
    average_cost_per_share = 0
    for line in lines:
        event, *args = line.split()
        if event == 'buy':
            shares, price = map(int, args)
            total_shares += shares
            average_cost_per_share = (average_cost_per_share * (total_shares - shares) + shares * price) / total_shares
        elif event == 'sell':
            shares, price = map(int, args)
            total_shares -= shares
            profit = (price - average_cost_per_share) * shares
            tax = 0.3 * profit
            profit -= tax
        elif event == 'split':
            x = int(args[0])
            average_cost_per_share, total_shares = process_split(average_cost_per_share, total_shares, x)
        elif event == 'merge':
            x = int(args[0])
            average_cost_per_share, total_shares = process_merge(average_cost_per_share, total_shares, x)
        elif event == 'die':
            shares, price = map(int, args)
            profit = (price - average_cost_per_share) * shares
            tax = 0.3 * profit
            profit -= tax
            return f"{profit:.8f}"

    return "0.00000000"

