def solution(stdin: str) -> str:
    # Read the input
    input_list = list(map(int, stdin.split()))
    n, m, D = input_list[0], input_list[1], input_list[2]
    edges = [(i, v_i, u_i) for i, (v_i, u_i) in enumerate(map(lambda x: tuple(sorted(map(int, x.split()))), stdin.splitlines()[1:]))]

    # Check if the task is impossible
    if D > n - 1:
        return "NO"

    # Create a graph
    graph = [[] for _ in range(n + 1)]
    for i, v_i, u_i in edges:
        graph[v_i].append(u_i)
        graph[u_i].append(v_i)

    # Find a spanning tree with the required degree for vertex 1
    visited = [False] * (n + 1)
    stack = [1]
    degree = 0
    while stack:
        vertex = stack.pop()
        if not visited[vertex]:
            visited[vertex] = True
            degree += 1
            for neighbor in graph[vertex]:
                if not visited[neighbor]:
                    stack.append(neighbor)

    if degree != D:
        return "NO"

    # Construct the spanning tree
    spanning_tree = []
    visited = [False] * (n + 1)
    stack = [1]
    while stack:
        vertex = stack.pop()
        if not visited[vertex]:
            visited[vertex] = True
            for neighbor in graph[vertex]:
                if not visited[neighbor]:
                    stack.append(neighbor)
                    spanning_tree.append((vertex, neighbor))

    # Find the edges from the spanning tree that are in the input edges
    result = []
    for i, v_i, u_i in edges:
        if (v_i, u_i) in spanning_tree or (u_i, v_i) in spanning_tree:
            result.append(f"{v_i} {u_i}\n")

    # Return the answer
    return "YES\n" + "".join(result)

