    # If the cat cannot eat all his food in one week,
    # then he cannot eat without additional food purchases
    if days_without_additional_purchases < days_in_week:
        return str(days_without_additional_purchases)

    # The cat can eat all his food in one week
    # Find the day of the week when the cat can eat the most
    max_days = 0
    for i in range(days_in_week):
        days = 0
        day = i
