    def is_valid(matrix, n, m, tile_types, n_tiles, limit):
        if not tile_types:
            return True

        for i in range(limit):
            for j in range(limit):
                if is_valid_rec(matrix, n, m, tile_types, n_tiles, i, j, 0):
                    return True
        return False

    def is_valid_rec(matrix, n, m, tile_types, n_tiles, i, j, idx):
        if idx == len(tile_types):
            return True

        if i + 1 > m or j + 1 > m:
            return False

        if not n_tiles[idx] or matrix[i][j] != -1:
            return is_valid_rec(matrix, n, m, tile_types, n_tiles, i, j + 1, idx + 1)

        for k in range(n):
            top_left, top_right, bottom_left, bottom_right = tile_types[k]
            if (matrix[i][j - 1] == top_left or matrix[i][j - 1] == top_right) and \
               (matrix[i - 1][j] == top_left or matrix[i - 1][j] == bottom_left) and \
               (matrix[i + 1][j] == bottom_right or matrix[i + 1][j] == bottom_left) and \
               (matrix[i][j + 1] == top_right or matrix[i][j + 1] == bottom_right):

                matrix[i][j] = k + 1
                n_tiles[idx][k] -= 1

                if is_valid_rec(matrix, n, m, tile_types, n_tiles, i, j + 1, idx + 1):
                    return True

                matrix[i][j] = -1
                n_tiles[idx][k] += 1

        return False

