                top_left, top_right = map(int, lines[case_id + i + 1].split())
                bottom_left, bottom_right = map(int, lines[case_id + n + i + 1].split())
                tile_types.append((top_left, top_right, bottom_left, bottom_right))
                n_tiles.append([1, 1, 1, 1])

            matrix = [[-1] * m for _ in range(m)]
            if is_valid(matrix, n, m, tile_types, n_tiles, m):
                result.append(f"Case #{case_id}: YES")
            else:
                result.append(f"Case #{case_id}: NO")

        return '\n'.join(result)

    return parse_input(stdin)

