    n = int(stdin.readline().strip())
    s = stdin.readline().strip()

    def is_sorted(coloring):
        for i in range(1, n):
            if coloring[i - 1] > coloring[i]:
                return False
        return True

    def can_swap(coloring, i, j):
        if i < 0 or j >= n:
            return False
        return coloring[i] != coloring[j]

    def find_min_colors(s):
        coloring = [0] * n
        max_color = 0
        for i in range(n):
            color = 1
            for j in range(max(0, i - 1), i):
                if s[j] > s[i] and color <= coloring[j]:
                    color = coloring[j] + 1
            coloring[i] = color
            max_color = max(max_color, color)
        return max_color, coloring

    min_colors, coloring = find_min_colors(s)

    if is_sorted(coloring):
        return f"{min_colors}\n{' '.join(map(str, coloring))}"

    for i in range(n - 1):
        if can_swap(coloring, i, i + 1):
            coloring[i], coloring[i + 1] = coloring[i + 1], coloring[i]
            if is_sorted(coloring):
                return f"{min_colors}\n{' '.join(map(str, coloring))}"
            coloring[i], coloring[i + 1] = coloring[i + 1], coloring[i]

    min_colors -= 1
    coloring = [c if c <= min_colors else min_colors + 1 for c in coloring]
    return f"{min_colors}\n{' '.join(map(str, coloring))}"

