    def factorial(n):
        return 1 if n == 0 else n * factorial(n - 1)

    def lexicographic_index(permutation):
        index = 0
        for i in range(N):
            inversions = 0
            for j in range(i):
                if permutation[j] > permutation[i]:
                    inversions += 1
            index += inversions * factorial(N - i - 1)
        return index

    a = lexicographic_index(P)
    b = lexicographic_index(Q)
    return abs(a - b)

