    def kruskal(parent, rank, edges):
        mst, i = [], 0
        edges = sorted(edges, key=lambda x: x[2])
        while i < n - 1:
            u, v, w = edges[i]
            if find(parent, u) != find(parent, v):
                union(parent, rank, u, v)
                mst.append(w)
                i += 1
            else:
                i += 1
        return mst

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)
        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1

    def find(parent, x):
        if parent[x] != x:
            parent[x] = find(parent, parent[x])
        return parent[x]

    n, m = map(int, stdin.readline().split())
    a = list(map(int, stdin.readline().split()))
    special_offers = [list(map(int, stdin.readline().split())) for _ in range(m)]

    edges = []
    for x, y, w in special_offers:
        edges.append((x - 1, y - 1, w))
    for i in range(n):
        for j in range(i + 1, n):
            edges.append((i, j, a[i] + a[j]))

    parent = list(range(n))
    rank = [0] * n
    mst = kruskal(parent, rank, edges)
    return str(sum(mst))

