    def find_set(parents, vertex):
        if parents[vertex] != vertex:
            parents[vertex] = find_set(parents, parents[vertex])
        return parents[vertex]

    def union_set(parents, size, vertex1, vertex2):
        root1, root2 = find_set(parents, vertex1), find_set(parents, vertex2)
        if root1 == root2:
            return False
        if size[root1] > size[root2]:
            root1, root2 = root2, root1
        parents[root1] = root2
        size[root2] += size[root1]
        return True

