        edges.append((u, v, w))

    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False
        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    edges.sort(key=lambda e: e[2])
    parent = list(range(n + 1))
    rank = [0] * (n + 1)
    mst_cost = 0
    mst_edges = []

    for u, v, w in edges:
        if union(parent, rank, u, v):
            mst_cost += w
            mst_edges.append((u, v, w))

    if len(set(parent[1:])) != 1:
        return "Impossible"

    for i in range(m):
        u, v, w = edges[i]
        parent2 = list(parent)
        rank2 = list(rank)
        if union(parent2, rank2, u, v):
            mst_cost2 = mst_cost - edges[i][2] + w
            for u, v, w in mst_edges:
                if w != edges[i][2]:
                    union(parent2, rank2, u, v)
            if len(set(parent2[1:])) != 1:
                return "Impossible"
            operations = 1
            for j in range(i + 1, m):
